SPTUtil.OutputSoundResourceData("sound/dialog/l1bm1/C_chores_1_convinit_Hom_L1.rsd", true) -- Hey I know you, you're in my dumb guy support group! Mind if I borrow your truck for a while?
SPTUtil.OutputSoundResourceData("sound/dialog/l1bm1/C_chores_2_convinit_Clt_L1.rsd", true) -- No can do, Mr. Fancy-Blue Pants!
SPTUtil.OutputSoundResourceData("sound/dialog/l1bm1/C_chores_3_convinit_Hom_L1.rsd", true) -- Fine, I'll help you with your hillbilly house chores!
SPTUtil.OutputSoundResourceData("sound/dialog/l1bm1/C_chores_4_convinit_Clt_L1.rsd", true) -- You help me with this year's harvest, and I'll be taking you anywhere you be needin to go!
SPTUtil.OutputSoundResourceData("sound/dialog/l1bm1/C_chores_5_convinit_Hom_L1.rsd", true) -- Woohoo!

SPTUtil.OutputSoundResourceData("sound/dialog/l1bm1/C_okay_1_noboxconv_Hom_L1B1.rsd", true)
SPTUtil.OutputSoundResourceData("sound/dialog/l1bm1/C_okay_2_noboxconv_Clt_L1B1.rsd", true) -- I want you to get me some of them cardie-board tubes, you know them? 
SPTUtil.OutputSoundResourceData("sound/dialog/l1bm1/C_okay_3_noboxconv_Hom_L1B1.rsd", true) -- Okay, I didn't ask for your life story!
SPTUtil.OutputSoundResourceData("sound/dialog/l1bm1/C_boring_1_noboxconv_Clt_L1B1.rsd", true) -- We need them roadkill, Mr. Fancy Blue Pants. 
SPTUtil.OutputSoundResourceData("sound/dialog/l1bm1/C_boring_2_noboxconv_Hom_L1B1.rsd", true) -- Boooorrrinnngggg....
SPTUtil.OutputSoundResourceData("sound/dialog/l1bm1/C_cityfolk_1_noboxconv_Clt_L1B1.rsd", true) -- Damn city folk, and your hostile takeovers!
SPTUtil.OutputSoundResourceData("sound/dialog/l1bm1/C_cityfolk_2_noboxconv_Apu_L1B1.rsd", true) -- Your cooperation would be appreciated!
SPTUtil.OutputSoundResourceData("sound/dialog/l1bm1/C_heehee_1_noboxconv_Hom_L1B1.rsd", true) -- Thank you, come again! Hee, hee, hee!